mapper.select:
SELECT c.id, c.name, dp.name AS departmentName, c.gender, c.price, c.is_active AS isActive FROM combination c JOIN department dp ON c.department_id = dp.id
c.department_id = #{departmentId}
AND c.is_active = #{isActive}
AND c.name LIKE CONCAT('%', #{name}, '%')

SELECT id, name, is_active AS isActive, gender, price, department_id AS departmentId
FROM Combination
WHERE id = #{id}

SELECT d.*
FROM detail d
WHERE d.department_id = #{departmentId}
  AND d.id NOT IN (
    SELECT cd.detail_id
    FROM combination_detail cd
    WHERE cd.combination_id = #{combinationId}
)
